import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { environment } from 'src/environments/environment';
import { Observable, Subject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ResultService {

  private conversionUrl:string = environment.baseUrl;
  private subject = new Subject<string>();

  constructor(private httpClient: HttpClient) { }

  sendClickEvent(value:string) {
    this.subject.next(value);
  }

  getClickEvent(): Observable<string>{
    return this.subject.asObservable();
  }

  public getConvertedWord(number:string){
    return this.httpClient.get<string>(`${this.conversionUrl}/${number}`)
  }

}
