import { Component, OnInit } from '@angular/core';
import { ResultService } from '../result/result.service';

@Component({
  selector: 'app-conversion-form',
  templateUrl: './conversion-form.component.html',
  styleUrls: ['./conversion-form.component.css']
})
export class ConversionFormComponent implements OnInit {

  public number:string = "";
  constructor(private resultService:ResultService) { }

  ngOnInit() {

  }

  onClickConvert(){
    this.resultService.onClickConversionFormEvent(this.number);
  }

}
<div class="form-group col-12">
  <input type="text" placeholder="Enter money" [(ngModel)]="number" class="form-control" id="money" >
  <div class="row">
    <div class="col text-center">
      <button type="submit" class="btn btn-sm btn-primary" (click)="onClickConvert()">Convert</button>
    </div>
  </div>
</div>
