import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-conversion-form',
  templateUrl: './conversion-form.component.html',
  styleUrls: ['./conversion-form.component.css']
})
export class ConversionFormComponent implements OnInit {

  constructor() { }

  ngOnInit() {
  }

}
<div class="form-group col-12">
  <label for="money">Money</label>
  <input type="text" placeholder="Enter money" class="form-control" id="money" >
  <div class="row">
    <div class="col text-center">
      <button type="submit" class="btn btn-sm btn-primary" >Convert</button>
    </div>
  </div>
</div>
